/**
 * @param {number} num - int[0,inf)
 * @param {?*} val
 * @return {*[]}
 */
export const createArray = (num, val) => {
  const _arr /* :*[] */ = []

  for (let i /* :number - int[0,inf) */ = 0; num > i; i++) {
    _arr[i] = val
  }

  return _arr
}

/**
 * @param {number} num - int[0,inf)
 * @param {number} [start] - int
 * @return {*[]}
 */
export const createSerialNumberArray = (num, start = 0) => {
  const _arr /* :*[] */ = []

  for (let i /* :number - int[0,inf) */ = 0; num > i; i++) {
    _arr[i] = start + i
  }

  return _arr
}

/**
 * @param {*[]} arr
 * @return {*[]}
 */
export const shuffleArray = (arr) => {
  let _n /* :number - int[0,inf) */ = arr.length
  let _t /* :number - int[0,inf) */ = 0
  let _i /* :number - int[0,inf) */ = 0

  while (_n) {
    _i = Math.floor(Math.random() * _n--)
    _t = arr[_n]
    arr[_n] = arr[_i]
    arr[_i] = _t
  }

  return arr
}

/**
 * @param {*[]} arr
 * @return {*[]}
 */
export const flattenArray = (arr) => {
  return arr.reduce((memo, val) => {
    return Array.isArray(val)
      ? memo.concat(flattenArray(val))
      : memo.concat(val)
  }, [])
}

/**
 * @param {*[]} arr
 * @param {number} num - int[0,inf)
 * @return {*[]}
 */
export const caterpillarArray = (arr, num) => {
  if (0 < num) {
    arr.push(...arr.splice(0, num))
  } else if (0 > num) {
    arr.unshift(...arr.splice(num, arr.length - num))
  }
  return arr
}

/**
 * @param {*} arg
 * @return {boolean}
 */
export const isJson = (arg) => {
  const _val /* :* */ = typeof arg === 'function' ? arg() : arg
  if (typeof _val !== 'string') {
    return false
  }
  try {
    JSON ? JSON.parse(_val) : eval(`(${_val})`)
    return true
  } catch (err) {
    return false
  }
}

/**
 * @param {number} offset - int
 * @return {number} int[0,inf)
 */
export const areaTimestamp = (offset) => {
  const _date /* :Date */ = new Date()
  const _utc /* :number - int[0,inf) */ = _date.getTime()
  const _deviceOffset /* :number - int */ = _date.getTimezoneOffset()
  return _utc + 1000 * 60 * (_deviceOffset + offset * 60)
}

/**
 * @param {number} offset - int
 * @return {Object}
 * @property {number} year - int[0,inf)
 * @property {number} day - int[0,6]
 * @property {number} date - int[1,31]
 * @property {number} month - int[0,11]
 * @property {number} hours - int[0,23]
 * @property {number} minutes - int[0,59]
 * @property {number} milliseconds - int[0,999]
 */
export const areaDate = (offset) => {
  const _offsetDate /* :Date */ = new Date(areaTimestamp(offset))

  return {
    year: _offsetDate.getFullYear(),
    day: _offsetDate.getDay(),
    date: _offsetDate.getDate(),
    month: _offsetDate.getMonth(),
    hours: _offsetDate.getHours(),
    minutes: _offsetDate.getMinutes(),
    milliseconds: _offsetDate.getMilliseconds()
  }
}

