    let _t = t
    if ((_t /= d / 2) < 1) {
      return (c / 2) * _t * _t + b
    }
    return (-c / 2) * (--_t * (_t - 2) - 1) + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeOutQuad: (t, b, c, d) => {
    let _t = t
    return -c * (_t /= d) * (_t - 2) + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeOutInQuad: (t, b, c, d) => {
    let _t = t
    if (_t < d / 2) {
      return -(c / 2) * (_t = (_t * 2) / d) * (_t - 2) + b
    }
    return (c / 2) * (_t = (_t * 2 - d) / d) * _t + (b + c / 2)
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeInQuart: (t, b, c, d) => {
    let _t = t
    return c * (_t /= d) * _t * _t * _t + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeInOutQuart: (t, b, c, d) => {
    let _t = t
    if ((_t /= d / 2) < 1) {
      return (c / 2) * _t * _t * _t * _t + b
    }
    return (-c / 2) * ((_t -= 2) * _t * _t * _t - 2) + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeOutQuart: (t, b, c, d) => {
    let _t = t
    return -c * ((_t = _t / d - 1) * _t * _t * _t - 1) + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeOutInQuart: (t, b, c, d) => {
    let _t = t
    if (_t < d / 2) {
      return -(c / 2) * ((_t = (_t * 2) / d - 1) * _t * _t * _t - 1) + b
    }
    return (c / 2) * (_t = (_t * 2 - d) / d) * _t * _t * _t + (b + c / 2)
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeInQuint: (t, b, c, d) => {
    let _t = t
    return c * (_t /= d) * _t * _t * _t * _t + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeInOutQuint: (t, b, c, d) => {
    let _t = t
    if ((_t /= d / 2) < 1) {
      return (c / 2) * _t * _t * _t * _t * _t + b
    }
    return (c / 2) * ((_t -= 2) * _t * _t * _t * _t + 2) + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeOutQuint: (t, b, c, d) => {
    let _t = t
    return c * ((_t = _t / d - 1) * _t * _t * _t * _t + 1) + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeOutInQuint: (t, b, c, d) => {
    let _t = t
    if (_t < d / 2) {
      return (c / 2) * ((_t = (_t * 2) / d - 1) * _t * _t * _t * _t + 1) + b
    }
    return (c / 2) * (_t = (_t * 2 - d) / d) * _t * _t * _t * _t + (b + c / 2)
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeInSine: (t, b, c, d) => {
    return -c * Math.cos((t / d) * (Math.PI / 2)) + c + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeInOutSine: (t, b, c, d) => {
    return (-c / 2) * (Math.cos((Math.PI * t) / d) - 1) + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeOutSine: (t, b, c, d) => {
    return c * Math.sin((t / d) * (Math.PI / 2)) + b
  },

  /**
   * @param {number} t - time
   * @param {number} b - begin
   * @param {number} c - change
   * @param {number} d - duration
   * @return {number}
   */
  easeOutInSine: (t, b, c, d) => {
    if (t < d / 2) {
      return (c / 2) * Math.sin(((t * 2) / d) * (Math.PI / 2)) + b
    }
    return (
      -(c / 2) * Math.cos(((t * 2 - d) / d) * (Math.PI / 2)) +
      c / 2 +
      (b + c / 2)
    )
  }
}

/**
