}

const toString /* :function */ = Object.prototype.toString

/**
 * @param {*} target
 * @return {string}
 */
export const objectType = (target) => {
  const _strs /* :string[] */ = toString.call(target).match(/^\[object (.+)\]$/)
  return _strs[1]
}

import { objectType } from './object-type'

/**
 * @param {Object} to
 * @param {Object} from
 */
const assign = (to, from) => {
  const _toKeys /* :string[] */ = Object.keys(to)
  for (const [key, val] of Object.entries(from)) {
    if (_toKeys.includes(key) && objectType(val) === 'Object') {
      assign(to[key], val)
    } else {
      to[key] /* :* */ = val
    }
  }
}

/**
 * @param {Object} to
 * @param {Object} from
 * @return {Object}
 */
export const deepAssign = (to, from) => {
  assign(to, from)
  return to
}

import { parse } from './query-string'

/**
 * @return {string}
 */
const _hashQuery = () => {
  const { hash } = location

  if (hash === '') {
    return ''
  }

  const _hashStrs /* :string[] */ = hash.match(/^.*\?(.*)$/)

  if (_hashStrs === null) {
    return ''
  }

  return _hashStrs[1]
}

/**
 * @param {boolean} [isHash]
 * @return {string}
 */
export const query = (isHash = false) => {
  return isHash ? _hashQuery() : location.search.replace('?', '')
}

/**
 * @param {string} query
 * @return {Object}
 */
export const toParams = (query) => {
  return query ? parse(query) : {}
}

/**
 * @param {boolean} [isHash]
 * @return {Object}
 */
export const params = (isHash = false) => {
  const _query /* :string */ = query(isHash)
  return _query ? parse(_query) : {}
}

/**
 * @param {string} name
 * @param {Object} [opts]
 * @param {?string} [opts.query]
 * @param {boolean} [opts.isHash]
 * @return {string}
 */
export const paramVal = (name, opts = {}) => {
  const { query = null, isHash = false } = opts

  const _query /* :string */ = query || query(isHash)

  if (!_query) return ''

  const _params /* :Object */ = parse(_query)

  if (typeof _params === 'undefined') return ''

  return _params[name]
}

import { easings } from './easing'
import { round } from './math'

/**
 * @param {string} easing
 * @param {number} time - int[0,inf)
 * @param {number} begin
 * @param {number} complete
 * @param {number} duration - int[0,inf)
 * @return {number}
 */
export const progress = (easing, time, begin, complete, duration) => {
  let _val /* :number */ = easings[easing](
    Math.min(time, duration),
    begin,
    complete - begin,
    duration
  )
  return round(_val)
}

/**
 * @param {string} str
 * @param {Object} [options]
 * @param {string} [options.sep]
 * @param {string} [options.eq]
 * @param {boolean} [options.typeChange]
 * @return {Object}
 */
export const parse = (str, options = {}) => {
  const { sep, eq, typeChange } = {
    sep: '&',
    eq: '=',
    typeChange: true,
    ...options
  }
  if (!str || typeof str !== 'string') {
    return {}
  }
  return String(str)
    .split(sep)
    .reduce((obj /* :Object */, param) => {
      const [key, val] = param.split(eq)
      const _val /* :string|number|boolean */ = (() => {
        if (!typeChange) return val
        if (val.match(/^[+,-]?([1-9]\d*|0)(\.\d+)?$/)) {
          return Number(val)
        }
        switch (val) {
          case 'true':
            return true
          case 'false':
            return false
          default:
            return val
        }
      })()
      obj[key] /* :string|number|boolean */ = _val
      return obj
    }, {})
}

/**
 * @param {Object} data
 * @param {Object} [options]
 * @param {string} [options.sep]
 * @param {string} [options.eq]
 * @return {string}
 */
export const serialize = (data, options = {}) => {
  const { sep, eq } = { sep: '&', eq: '=', ...options }
  const _encode /* :function */ = encodeURIComponent
  let _query /* :string */ = ''

  Object.entries(data).forEach(([key, val]) => {
    const _type /* :string */ =
      typeof val === 'object' && val instanceof Array ? 'array' : typeof val

    switch (_type) {
      case 'undefined':
        _query += key
        break
      case 'array':
        val.forEach((_i) => {
          _query += `${key}[]${eq + _encode(val[_i]) + sep}`
        })
        _query = _query.substr(0, _query.length - 1)
        break
      case 'object':
        Object.entries(val).forEach(([key_, val_]) => {
          _query += `${key}[${key_}]${eq + _encode(val_) + sep}`
        })
        _query = _query.substr(0, _query.length - 1)
        break
      default:
        _query += key + eq + _encode(val)
        break
    }
    _query += sep
  })
  return _query.substr(0, _query.length - 1)
}

/**
 * @param {number} num
 * @param {number} [min]
 * @return {number}
 */
export const random = (num, min = 0) => {
  return Math.random() * (num - min) + min
}

/**
 * @param {number} num - int
 * @param {number} [min] - int
 * @return {number} int
 */
export const randomInt = (num, min = 0) => {
  return Math.floor(random(num + 1, min))
}

/**
 * @return {number} -1|1
 */
export const randomDir = () => {
  return randomInt(1) ? 1 : -1
}

import { numberStrings, notNumberStrings } from './string'

/**
 * @param {string} before
 * @param {string} after
 * @param {number} ratio - [0,1]
 */
export const ratioValue = (before, after, ratio) => {
  const _strings /* :string[] */ = notNumberStrings(before)
  const _beforeNumbers /* :number[] */ = numberStrings(before).map((s) =>
    Number(s)
  )
  const _afterNumbers /* :number[] */ = numberStrings(after).map((s) =>
    Number(s)
  )

  const _changeNumbers /* :number[] */ = []
  for (let i /* :number - int[0,inf) */ = 0; _beforeNumbers.length > i; i++) {
    const _change /* :number[] */ =
      (_afterNumbers[i] - _beforeNumbers[i]) * ratio
    _changeNumbers.push(_beforeNumbers[i] + _change)
  }

  let _val /* :string */ = _strings[0]
  for (let i /* :number - int[0,inf) */ = 0; _changeNumbers.length > i; i++) {
    _val += _changeNumbers[i].toString() + _strings[i + 1]
  }

  return _val
}

/**
 * @param {number} width - [0,inf)
 * @param {number} height - [0,inf)
 * @param {number} widthWidthRate - viewport width rate width / height [0,inf)
 * @return {Object}
 * @property {number} width - [0,inf)
 * @property {number} height - [0,inf)
 * @property {number} x
 * @property {number} y
 */
export const cover = (width, height, widthWidthRate) => {
  const _orignRate /* :number - [0,inf) */ = width / height

  if (widthWidthRate < _orignRate) {
    const _height /* :number - [0,inf) */ = width / widthWidthRate
    return {
      width: width,
      height: _height,
      x: 0,
      y: (height - _height) / 2
    }
  } else {
    const _width /* :number - [0,inf) */ = height * widthWidthRate
    return {
      width: _width,
      height: height,
      x: (width - _width) / 2,
      y: 0
    }
  }
}

/**
 * @param {number} w - [0,inf)
 * @param {number} h - [0,inf)
 * @return {Object}
 * @property {number} max - [1,inf)
 * @property {number} width - [1,inf)
 * @property {number} height - [1,inf)
 */
export const maxRatio = (w, h) => {
  const _maxRatio /* :number - [1,inf) */ = Math.max(w / h, h / w)
  return {
    raito: _maxRatio,
    width: w > h ? _maxRatio : 1,
    height: h > w ? _maxRatio : 1
  }
}

/**
 * @param {number} w - [0,inf)
 * @param {number} h - [0,inf)
 * @return {Object}
 * @property {number} min - [0,1]
 * @property {number} width - [0,1]
 * @property {number} height - [0,1]
 */
export const minRatio = (w, h) => {
  const _minRatio /* :number - [0,1] */ = Math.min(w / h, h / w)
  return {
    raito: _minRatio,
    width: w > h ? 1 : _minRatio,
    height: h > w ? 1 : _minRatio
  }
}

/**
 * squares
 */

/**
 * @param {number} col - int[0,inf)
 * @param {number} x - int[0,inf)
 * @param {number} y - int[0,inf)
 * @return {number} int[0,inf)
 */
export const squaresIndex = (col, x, y) => {
  return col * y + x
}

/**
 * @param {number} col - int[0,inf)
 * @param {number} index - int[0,inf)
 * @return {number[]} int[0,inf)
 * @property {number} - int[0,inf)  x position
 * @property {number} - int[0,inf)  y position
 */
export const squaresPosition = (col, index) => {
  const y /* :number - int[0,inf) */ = Math.floor(index / col)
  const x /* :number - int[0,inf) */ = index - col * y
  return [x, y]
}

/**
 * @param {string} str
 * @return {string}
 */
export const toHalfNum = (str) => {
  return str.replace(/[-]/g, (s) => {
    return String.fromCharCode(s.charCodeAt(0) - 0xfee0)
  })
}

/**
 * @param {string} str
 * @return {string}
 */
export const toCommaNum = (str) => {
  const _strs /* :string[] */ = str.split('.')
  if (!_strs[0] || !_strs[0].match(/[0-9]+/)) {
    throw new Error(`'${_strs[0]}' is not number string.`)
  }
  const _decimal /* :string */ = _strs[1]
  const _integer /* :string */ = _strs[0]
    .replace(/,/g, '')
    .replace(/(\d)(?=(\d{3})+$)/g, '$1,')
  return _decimal ? `${_integer}.${_decimal}` : _integer
}

/**
 * @param {string} str
 * @param {number} digit - int[0,inf)
 * @return {string}
 */
export const toZeroPadding = (str, digit) => {
  const _digit /* :number - int[0,inf) */ = str.split('.')[0].length
  const _diff /* :number - int */ = digit - _digit
  let _zero /* :string */ = ''
  for (let i /* :number - int[0,inf) */ = 0; _diff > i; i++) {
    _zero += '0'
  }
  return _zero + str
}

/**
 * @param {string} str
 * @return {string}
 */
export const ucfirst = (str) => {
  return str.charAt(0).toUpperCase() + str.slice(1)
}

/**
 * @param {string} str
 * @return {string}
 */
export const toFirstLetterUpper = (str) => {
  return str.charAt(0).toUpperCase() + str.substring(1).toLowerCase()
}

/**
 * @param {string} str
 * @param {string} sep
 * @return {string}
 */
export const toPascalCase = (str, sep) => {
  return str.replace(new RegExp(`${sep}.`, 'g'), (s) /* :string */ => {
    return s.charAt(1).toUpperCase()
  })
}

/**
 * @param {string} str
 * @param {string} sep
 * @return {string}
 */
export const pascalCaseTo = (str, sep) => {
  return str.replace(/([A-Z])/g, (s) /* :string */ => {
    return sep + s.charAt(0).toLowerCase()
  })
}

/**
 * @param {string} str
 * @return {number}
 */
export const charCount = (str) => {
  let _count /* :number - int[0,inf) */ = 0
  const _escapeStr /* :string */ = escape(str)
  for (
    let i /* :number - int[0,inf) */ = 0;
    i < _escapeStr.length;
    i++, _count++
  ) {
    if (_escapeStr.charAt(i) === '%') {
      if (_escapeStr.charAt(++i) === 'u') {
        i += 3
        _count++
      }
      i++
    }
  }
  return _count
}

/**
 * @param {string} str
 * @return {string[]}
 */
export const numberStrings = (str) => {
  return str.match(/-?[0-9]+\.?[0-9]*|-?[0-9]*\.?[0-9]+/g)
}

/**
 * @param {string} str
 * @return {string[]}
 */
export const notNumberStrings = (str) => {
  return str.split(/-?[0-9]+\.?[0-9]*|-?[0-9]*\.?[0-9]+/g)
}

import { charCount } from './string'

