import { round } from '../models/math'
import { progress } from '../models/progress'
import { rect } from '../utils/rect'
import { requestAnimationFramer } from '../modules'
import { store } from '../store'

class Anchor {
  _duration /* :number - int[0,inf) */ = 0
  _easing /* :string */ = ''
  _startTime /* :number - int[0,inf) */ = 0

  /**
   * @return {Object}
   */
  static get _defaultOptions() {
    return {
      duration: 1000,
      easing: 'easeInOutCubic'
    }
  }

  /**
   * @param {Object} [options]
   */
  constructor(options = {}) {
    const { duration, easing } = { ...Anchor._defaultOptions, ...options }

    this._duration = duration
    this._easing = easing
  }

  /**
   * @param {Element} $target
   */
  scroll($target) {
    const _offsetY = store.state.windowOffsetY
    const _rectTop = round(rect($target).top)

    this._startTime = Date.now()

    requestAnimationFramer.add(
      this,
      this._animate.bind(this, _offsetY, _rectTop)
    )
  }

  /**
   * @param {number} begin - int(-inf,inf)
   * @param {number} complete - int(-inf,inf)
   */
  _animate(begin, complete) {
    const _time /* :number - int[0,inf) */ = Date.now() - this._startTime
    const _val /* :number */ = progress(
      this._easing,
      _time,
      begin,
      complete,
      this._duration
    )

    window.scrollTo(0, _val)

    if (complete === _val) {
      requestAnimationFramer.remove(this)
    }
  }
}

export { Anchor as default }

class ControlledRequestAnimationFramer {
  _animes /* :Map */ = new Map()
  _animateHandle /* :number - int[0,inf) */ = 0

  /**
   * @param {Object} key
   * @return {number} [0,inf)
   */
  getTime(key) {
    return this._animes.get(key).time
  }

  /**
   * @param {Object} key
   * @param {function} func
   * @param {number} fps - int[0,inf)
   * @param {number} [interval] - int[0,inf)
   * @return {Instance}
   */
  add(key, func, fps, interval = 0) {
    this._animes.set(key, {
      startTime /* :number - [0,inf) */: new Date().getTime(),
      time /* :number - [0,inf) */: 0,
      frameStartTime /* :number - [0,inf) */: 0,
      ms /* :number - [0,inf) */: fps === 'auto' ? 0 : Math.floor(1000 / fps),
      count /* :number - int[0,inf) */: 0,
      interval /* :number - int[0,inf) */: interval + 1,
      func /* :function */: func
    })

    if (!this._animateHandle) {
      this._start()
    }

    return this
  }

  /**
   * @param {Object} key
   * @return {Instance}
   */
  remove(key) {
    this._animes.delete(key)
    if (!this._animes.size) {
      this._stop()
    }

    return this
  }

  _start() {
    if (this._animateHandle) {
      return
    }
    this._animate()
  }

  _stop() {
    cancelAnimationFrame(this._animateHandle)
    this._animateHandle = 0
  }

  _animate() {
    this._animateHandle = requestAnimationFrame(this._animate.bind(this))

    const _time = new Date().getTime()

    this._animes.forEach(({ frameStartTime, ms, count, interval, func }) => {
      const _anime /* :Object */ = this._animes.get(key)
      _anime.count++
      _anime.time = _time - _anime.startTime
      if (count % interval || ms > _time - frameStartTime) {
        return
      }
      _anime.frameStartTime = _time
      func(key)
    })
  }
}

export { ControlledRequestAnimationFramer as default }

import { createEvent } from '../utils/event'

class Eventer {
  _events /* Object[] */ = []

  /**
   * @param {Element} target
   * @param {string} eventType
   * @param {function} listener
   * @param {boolean|Object} [option]
   * @return {Object}
   * @property {functon} add
   * @property {functon} remove
   */
  create(target, eventType, listener, option = false) {
    return {
      add: () => {
        this.add(target, eventType, listener, option)
      },
      remove: () => {
        this.remove(target, eventType, listener)
      }
    }
  }

  /**
   * @param {Element} target
   * @param {string} eventType
   * @param {function} listener
   * @param {boolean|Object} [option]
   * @return {Instance}
   */
  add(target, eventType, listener, option = false) {
    let _hasEvent /* :boolean */ = false

    for (const e of this._events) {
      if (
        target === e.target &&
        eventType === e.eventType &&
        option === e.option &&
        !e.listeners.includes(listener)
      ) {
        e.listeners.push(listener)
        _hasEvent = true
        return
      }
    }

    if (!_hasEvent) {
      const _listeners /* :function[] */ = [listener]
      this._events.push({
        target,
        eventType,
        option,
        listeners: _listeners,
        event: this._addEvent(target, eventType, _listeners, option)
      })
    }

    return this
  }

  /**
   * @param {Element} target
   * @param {string} eventType
   * @param {function} listener
   * @return {Instance}
   */
  remove(target, eventType, listener) {
    this._events.forEach((e, i) => {
      if (
        target === e.target &&
        eventType === e.eventType &&
        e.listeners.includes(listener)
      ) {
        e.listeners.splice(e.listeners.indexOf(listener), 1)
        if (!e.listeners.length) {
          this._removeEvent(i)
        }
        return
      }
    })

    return this
  }

  /**
   * @param {Element} target
   * @param {string} eventType
   * @param {function[]} listeners
   * @param {boolean|Object} option
   * @return {Object}
   * @property {function} add
   * @property {function} remove
   */
  _addEvent(target, eventType, listeners, option) {
    const _event = createEvent(
      target,
      eventType,
      (e) => {
        for (const listener of listeners) {
          listener(e)
        }
      },
      option
    )
    _event.add()
    return _event
  }

  /**
   * @param {number} index - int
   */
  _removeEvent(index) {
    this._events.splice(index, 1)
  }
}

export { Eventer as default }

import { getBackgroundImages } from '../utils/background-image'
import { loadImage } from '../utils/load'
import { wait } from '../utils/wait'

class ImagePreloader {
  _status /* :WeakMap */ = new WeakMap()
  _images /* :Map */ = new Map()

  /**
   * @return {Object}
   */
  static get _defaultOptions() {
    return {
      images: [],
      needsElementLoad: false
    }
  }

  /**
   * @param {Element} $el
   * @param {Object} [options]
   * @param {Element[]} [options.images]
   * @param {boolean} [options.needsElementLoad]
   * @return {Promsie}
   */
  async add($el, options = {}) {
    const { images, needsElementLoad } = {
      ...ImagePreloader._defaultOptions,
      ...options
    }

    const _images /* :Set */ = new Set()

    // init images
    for (const img of images) {
      if (!this._images.has(img)) {
        _images.add(img)
      }
    }

    // wrap element background image
    for (const bgImg of getBackgroundImages($el)) {
      if (bgImg && !this._images.has(bgImg)) {
        _images.add(bgImg)
      }
    }

    // child elements background images
    for (const $child of $el.getElementsByTagName('*')) {
      for (const bgImg of getBackgroundImages($child)) {
        if (bgImg && !this._images.has(bgImg)) {
          _images.add(bgImg)
        }
      }
    }

    if (needsElementLoad) {
      // img elements images
      await Promise.all(
        Array.from($el.getElementsByTagName('img'), async ($img) => {
          if (!$img.getAttribute('src')) {
            return
          }
          let _img /* :string */ = ''
          await wait(200, () /* :string */ => {
            _img =
              typeof $img.currentSrc === 'string' ? $img.currentSrc : $img.src
            return _img
          })
          if (_img && !this._images.has(_img)) {
            _images.add(_img)
          }
        })
      )

      // video elements posters
      Array.from($el.getElementsByTagName('video'), ($video /* :Element */) => {
        const _poster /* :string */ = $video.poster
        if (_poster && !this._images.has(_poster)) {
          _images.add(_poster)
        }
      })

      // svg image elements images
      Array.from($el.getElementsByTagName('image'), ($img) => {
        const _img /* :string */ = $img.getAttribute('xlink:href')
        if (_img && !this._images.has(_img)) {
          _images.add(_img)
        }
      })
    }

    this._status.set($el, {
      images /* :Set */: _images,
      loadedLen /* :number - int[0,inf) */: 0
    })
  }

  /**
   * @param {Element} $el
   * @return {Instance}
   */
  remove($el) {
    this._status.delete($el)
    return this
  }

  /**
   * @param {Element} $el
   * @return {Promise}
   */
  load($el) {
    const _loaders = this.getLoaders($el)
    return Promise.all(_loaders.map((loader) /* :Promise */ => loader()))
  }

  /**
   * @param {Element} $el
   * @return {Array<function>}
   */
  getLoaders($el) {
    const _images /* :Set */ = this._status.get($el).images

    return [..._images].map((src) => {
      return this._load.bind(this, $el, src)
    })
  }

  /**
   * @param {Element} $el
   * @return {Array<string>}
   */
  getImages($el) {
    return this._status.get($el).images
  }

  /**
   * @param {Element} $el
   * @return {number}
   */
  getLoadedLen($el) {
    return this._status.get($el).loadedLen
  }

  /**
   * @param {Element} $el
   * @param {string} src
   * @return {Promise}
   */
  _load($el, src) {
    return loadImage(src, {
      done: (img) => {
        this._images.set(src, {
          src /* :string */: img.src,
          width /* :number - int[0,inf) */: img.width,
          height /* :number - int[0,inf) */: img.height
        })
      },
      always: () => {
        this._status.get($el).loadedLen++
      }
    })
  }
}

export { ImagePreloader as default }

class InviewportObserver {
  _observer /* :IntersectionObserver */
  _functions /* :WeakMap */ = new WeakMap()

  /**
   * @return {Instance}
   */
  create() {
    this._observer = new IntersectionObserver(this._update.bind(this))
    return this
  }

  /**
   * @return {Instance}
   */
  destroy() {
    this._observer = null
    return this
  }

  /**
   * @param {Element} $el
   * @param {function} func
   * @return {Instance}
   */
  add($el, func) {
    this._functions.set($el, func)
    this._observer.observe($el)
    return this
  }

  /**
   * @param {Element} $el
   * @return {Instance}
   */
  remove($el) {
    this._functions.delete($el)
    this._observer.unobserve($el)
    return this
  }

  /**
   * @param {Array<IntersectionObserverEntry>} entries
   */
  _update(entries) {
    for (const { target, isIntersecting } of entries) {
      this._functions.get(target)(isIntersecting)
    }
  }
}

export { InviewportObserver as default }

