export const splitElement = (htmlStr, options = {}) => {
  const _defaultOptions = {
    wrapTag: 'span',
    className: '',
    attributes: {},
    type: 'character',
    nest: 1
  }

  const { wrapTag, className, attributes, type, nest } = {
    ..._defaultOptions,
    ...options
