  let _charCnt /* :number - int[0,inf) */ = 0

  const _append = (
    str /* :string */,
    brIndex /* :number - int[0,inf) */,
    wordIndex /* :number - int[0,inf) */,
    charCnt /* :number - int[0,inf) */,
    isSpace /* :boolean */ = false
