      let _$wrap /* :Element */ = _$char
      let _$inner /* :Element|null */ = null
      for (let i = 0; nest - 1 > i; i++) {
        _$inner = document.createElement(wrapTag)
        _$wrap.appendChild(_$inner)
        _$wrap = _$inner
