/**
 * @param {Object} [options]
 * @param {number} [options.interval] - int[0,inf)
 * @param {boolean} [options.isLastRun]
 * @return {function(fn: function)}
 */
export const throttle = (options = {}) => {
  const { interval, isLastRun } = {
    interval: 100,
    isLastRun: true,
    ...options
  }

  let _lastTime /* :number - int[0,inf) */ = new Date().getTime() - interval
  let _timer /* :number - int[0,inf) */ = 0

  return (fn) => {
    if (_lastTime + interval <= new Date().getTime()) {
      _lastTime = new Date().getTime()
      fn()
    }
    if (isLastRun) {
      clearTimeout(_timer)
      _timer = setTimeout(() => {
        fn()
        _timer = 0
      }, interval)
    }
  }
}

/**
 * @param {number} time - int[0,inf)
 * @return {Promise}
 */
export const waitTime = (time) => {
  return new Promise((resolve) => {
    setTimeout(resolve, time)
  })
}

/**
 * @param {number} timer - int[0,inf)
 * @param {number} startTime - int[0,inf)
 * @param {number} interval - int[0,inf)
 * @param {number} timeout - int[0,inf)
 * @param {function} observe
 * @param {function} resolve
 * @param {function} returnValFn
 * @return {Promise}
 */
const check /* :function */ = (
  timer,
  startTime,
  interval,
  timeout,
  observe,
  resolve,
  returnValFn
) => {
  if (timer) {
    clearTimeout(timer)
  }

  timer = setTimeout(() => {
    if (observe() || timeout < new Date().getTime() - startTime) {
      returnValFn ? resolve(returnValFn()) : resolve()
    } else {
      check(timer, startTime, interval, timeout, observe, resolve, returnValFn)
    }
  }, interval)
}

/**
 * @param {number} interval - int[0,inf)
 * @param {function} observe
 * @param {Object} [options]
 * @param {number} [options.timeout] - int[0,inf)
 * @param {?function} [options.returnValFn]
 * @return {Promise}
 */
export const wait = (interval, observe, options = {}) => {
  let { timeout, returnValFn } = {
    timeout: 30000,
    returnValFn: null,
    ...options
  }

  let _timer /* :number - [0,inf) */ = 0
  const _startTime /* :number - [0,inf) */ = new Date().getTime()

  return new Promise((resolve) => {
    check(_timer, _startTime, interval, timeout, observe, resolve, returnValFn)
  })
}

